mapper.select:
select
id, menu_name, parent_id, order_num, menu_type, path, name, icon, perms, visible, component
from jdboot.sys_menu


        and id = #{id}


        and menu_name = #{menuName}


        and parent_id = #{parentId}


        and order_num = #{orderNum}


        and menu_type = #{menuType}


        and path = #{path}


        and name = #{name}


        and icon = #{icon}


        and perms = #{perms}


        and visible = #{visible}


        and component = #{component}
