mapper.select:
select
id, role_name, create_people, create_date, delete_flag, update_people, update_date
from jdboot.sys_role

    delete_flag = 0

        and id = #{id}


        and role_name = #{roleName}


        and create_people = #{createPeople}


        and create_date = #{createDate}


        and update_people = #{updatePeople}


        and update_date = #{updateDate}
